import React, { useState } from 'react';

import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import './Expenses.css';
import ExpensesFilter from './ExpensesFilter'


const Expenses = (props) => {
    const [filteredYear, setFilteredYear] = useState('2022');

    const filterChangeHandler = selectedYear => {
        setFilteredYear(selectedYear);

    };

    return (
        <div>
            <Card className='expenses'>
                {props.items.map((expnce) => (<ExpensesFilter selected={filteredYear} onChangeFilter={filterChangeHandler} />))}
                {props.items.map((expense) => (<ExpenseItem key={expense.id} title={expense.title} amount={expense.amount} date={expense.date} />))}
            </Card>
        </div>
    )
}

export default Expenses
