import React, { useState } from "react";
import './ExpenseForm.css';

const ExpenseForm = () => {
    const [enteredTitle, setEnteredTitle] = useState('');
    const [enteredEmount, setEnteredAmount] = useState('');
    const [enteredDate, setEnteredDate] = useState('')

    const [userInput, setUserInput] = useState({
        enteredTitle:'',
        enteredEmount:'',
        enteredDate:'',
    });

    const amountChangeHandler = (e) => {
        setEnteredAmount(e.target.value)
        setUserInput((preveState) =>{
            return {...preveState, enteredTitle:e.targer.value}
        })
    }
    const titleChangeHandler = (e) => {
        setEnteredTitle(e.target.value);
        // setUserInput((preveState) =>{
        //     return {...preveState, enteredTitle:e.targer.value}
        // })
    }
    const dateChanheHandler = (e) => {
        setEnteredDate(e.targer.value)
        // setUserInput((preveState) =>{
        //     return {...preveState, enteredTitle:e.targer.value}
        // })
    }
    return (

        <form className="">
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type='text' onChange={titleChangeHandler} />
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input type='number' onChange={amountChangeHandler} min='0.01' step='0.01' />
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input type='date' onChange={dateChanheHandler} min='2022-11-17' max='2024-01-01' />
                </div>
            </div>
            <div className="new-expense__actions">
                <button type="submit"> Add expense</button>
            </div>
        </form>

    )

}

export default ExpenseForm
